sys_ws_operation.operation_script:
(function process( /*RESTAPIRequest*/ request, /*RESTAPIResponse*/ response) {

    var requestNIAssignedByQuery = request.queryParams.ni_assigned_query;
    var userNIAssignedByCount = new GlideAggregate('x_58872_needit_needit');
    userNIAssignedByCount.addAggregate('COUNT');
    //userNIAssignedByCount.addQuery('assigned_to', 'assigned_toISEMPTY');
    userNIAssignedByCount.addEncodedQuery(requestNIAssignedByQuery);
    userNIAssignedByCount.query();

    var needitRecs = 0;
    var niUserRecs = [];
    var needitRecsCount = 0;

    while (userNIAssignedByCount.next()) {
        needitRecs = userNIAssignedByCount.getAggregate('COUNT');
        var assignTo = userNIAssignedByCount.assigned_to.getDisplayValue();

        niUserRecs.push({
            "assignedTo": assignTo,
            "count": needitRecs
        });
        needitRecsCount = parseInt(needitRecsCount) + parseInt(needitRecs);
    }

    //Create a body object.  Add property value pairs to the body.
    var body = {};
    body.totalUserNIRecs = needitRecsCount;
    body.catCounts = niUserRecs;

    // Send the response object, which is returned to the requestor, to the body object.
    response.setBody(body);


})(request, response);
